import pygame
pygame.init()
screen = pygame.display.set_mode([1130, 647])
pygame.display.set_caption("My Game")
time=pygame.time.Clock()
bird = pygame.image.load((r"C:\Users\A200162668\Desktop\Hauptordner\Python\.vscode\pythonlernen\pythonbetrieb\tests\flappybird\bird.png")).convert()
bird.set_colorkey([236,28,36])
bird.set_colorkey([159,51,55])
bird.set_colorkey([190,59,43])
done = False
while done==False:
    for event in pygame.event.get():
        if event.type == pygame.QUIT:
            done = True
        elif event.type == pygame.KEYDOWN:
            if event.key == pygame.K_ESCAPE:
                done = True
    screen.fill((0, 0, 0))
    screen.blit(bird, [0, 0])
    pygame.display.flip()
time.tick(20)
pygame.quit()
